      articleContent:'',
    };
  },
  computed: {
    ...mapState(['articleContent', 'flag', 'preview']) 
  },
  methods: {
    cancelArticle() {
      this.setflag(false); 
      this.setpreview(false); 
    },
    previewArticle() {
      this.setpreview(!this.preview);
    },
    saveArticle() {
      this.setflag(false); 
      this.setpreview(false);
    },
    ...mapMutations(['setflag', 'setpreview']) 
  },
  mounted() {
  }
};
</script>

